import React, { Component } from "react";
import axios from 'axios';
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

export default class CreateBreakdown extends Component {
  constructor(props) {
    super(props);

    this.onChangeUsername = this.onChangeUsername.bind(this);
    this.onChangeDescription = this.onChangeDescription.bind(this);
    this.onChangeBreakdowntime = this.onChangeBreakdowntime.bind(this);
    this.onChangeYoutubeurl = this.onChangeYoutubeurl.bind(this);
    this.onChangeDate = this.onChangeDate.bind(this);
    this.onSubmit = this.onSubmit.bind(this);

    this.state = {
      username: '',
      description: '',
      breakdowntime: 0,
      youtubeurl: '',
      date: new Date(),
      users: []
    }
  }

  componentDidMount() {
    axios.get('http://localhost:5000/users/')
      .then(response => {
        if (response.data.length > 0) {
          this.setState({
            users: response.data.map(user => user.username), // Data = Array, map = allow us to return something for every element in the array. This case user -> username
            username: response.data[0].username
          })
        }
      })
      .catch((error) => {
        console.log(error);
      })

  }

  onChangeUsername(e) {
    this.setState({
      username: e.target.value
    })
  }

  onChangeDescription(e) {
    this.setState({
      description: e.target.value
    })
  }

  onChangeBreakdowntime(e) {
    this.setState({
      breakdowntime: e.target.value
    })
  }

  onChangeYoutubeurl(e) {
    this.setState({
      youtubeurl: e.target.value
    })
  }

  onChangeDate(date) {
    this.setState({
      date: date
    })
  }

  onSubmit(e) {
    e.preventDefault();

    const breakdown = {
      username: this.state.username,
      description: this.state.description,
      breakdowntime: this.state.breakdowntime,
      youtubeurl: this.state.youtubeurl,
      date: this.state.date
    }

    console.log(breakdown);

    axios.post('http://localhost:5000/breakdowns/add', breakdown)
    .then(res => console.log(res.data));

    window.location = '/';
  }

  render() {
    return (
    <div>
      <h3>Add New Breakdown</h3>
      <form onSubmit={this.onSubmit}>
        <div className="form-group"> 
          <label>Username: </label>
          <select ref="userInput"
              required
              className="form-control"
              value={this.state.username}
              onChange={this.onChangeUsername}>
              {
                this.state.users.map(function(user) {
                  return <option 
                    key={user}
                    value={user}>{user}
                    </option>;
                })
              }
          </select>
        </div>
        <div className="form-group"> 
          <label>Music: </label>
          <input  type="text"
              required
              className="form-control"
              value={this.state.description}
              onChange={this.onChangeDescription}
              />
        </div>
        <div className="form-group">
          <label>Breakdown time (in minutes): </label>
          <input 
              type="text" 
              className="form-control"
              value={this.state.breakdowntime}
              onChange={this.onChangeBreakdowntime}
              />
        </div>
        <div className="form-group"> 
          <label>Youtube: </label>
          <input  type="text"
              required
              className="form-control"
              value={this.state.youtubeurl}
              onChange={this.onChangeYoutubeurl}
              />
        </div>
        <div className="form-group">
          <label>Date: </label>
          <div>
            <DatePicker
              selected={this.state.date}
              onChange={this.onChangeDate}
            />
          </div>
        </div>

        <div className="form-group">
          <input type="submit" value="Add breakdown" className="btn btn-primary" />
        </div>
      </form>
    </div>
    )
  }
}
